package app.model;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class DataBaseHandler extends dbConfig {    Connection dbConnectiod;    public Connection getDbConnectiod() throws ClassNotFoundException, SQLException {        String connectionString = "jbdc:mysql//" + dbHost + ":" + dbPort + "/" + dbName;        Class.forName("com.mysql.jdbc.Driver");        dbConnectiod = DriverManager.getConnection(connectionString, dbUser, dbPassword);        return dbConnectiod;    }}