package app.servlets;import app.entities.User;import app.model.Model;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;public class AuthenticationServlet extends HttpServlet {@Overrideprotected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {    request.setCharacterEncoding("UTF-8");    RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/authentication.jsp");    requestDispatcher.forward(request,response);}    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        String name = request.getParameter("name");        String password = request.getParameter("password");        User user = new User(name, password);        Model model = Model.getInstance();        if(model.isExist(user)) {            System.out.print("ВХОД ВЫПОЛНЕН!");            RequestDispatcher requestDispatcher = request.getRequestDispatcher("index.html");            requestDispatcher.forward(request,response);        }        else {            request.setAttribute("name", name);            doGet(request, response);        }    }}