package app.servlets;import app.model.DataBaseHandler;import app.model.Model;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;import java.util.ArrayList;import java.util.List;public class ListServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        DataBaseHandler dataBaseHandler = new DataBaseHandler();        ArrayList<String> names = new ArrayList<>();        try {            names = dataBaseHandler.getAllUserNames();        } catch (SQLException e) {            e.printStackTrace();        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        request.setAttribute("userNames", names);        RequestDispatcher requestDispatcher = request.getRequestDispatcher("views/list.jsp");        requestDispatcher.forward(request,response);    }}